(function() {
  var App, Pry, pry;

  App = require('./pry/app');

  Pry = (function() {
    function Pry(arg) {
      var repl;
      this.isStandAlone = (arg != null ? arg : {}).isStandAlone;
      repl = function(_) {
        return pry.open((function(_this) {
          return function(input) {
            return _ = eval(input);
          };
        })(this));
      };
      this.it = '(' + repl.toString() + ').call(this)';
    }

    Pry.prototype.open = function(scope) {
      var app;
      app = new App(scope, this.isStandAlone);
      return app.open();
    };

    return Pry;

  })();

  module.exports = new Pry;

  if (require.main === module) {
    eval((pry = new Pry({
      isStandAlone: true
    })).it);
  }

}).call(this);
